define([
    'jquery',
    'mage/translate',
    'mage/backend/validation'
], function ($) {
    'use strict';

    return function () {
        $.validator.addMethod('validate-cron-format',
            function (value, element) {
                var inputVal  = $.trim($(element).val()),
                    cronregex = new RegExp(/^(\*|([0-9]|1[0-9]|2[0-9]|3[0-9]|4[0-9]|5[0-9])|\*\/([0-9]|1[0-9]|2[0-9]|3[0-9]|4[0-9]|5[0-9])) (\*|([0-9]|1[0-9]|2[0-3])|\*\/([0-9]|1[0-9]|2[0-3])) (\*|([1-9]|1[0-9]|2[0-9]|3[0-1])|\*\/([1-9]|1[0-9]|2[0-9]|3[0-1])) (\*|([1-9]|1[0-2])|\*\/([1-9]|1[0-2])) (\*|([0-6])|\*\/([0-6]))$/);

                return cronregex.test(inputVal);
            },

            $.mage.__('Please enter the correct cron format.')
        );
    };
});
